parameters.jsCode:
// –ë–µ—Ä—ë–º JSON –æ—Ç –∞–≥–µ–Ω—Ç–∞: –ø–æ–ª–µ "output" ‚Äî —ç—Ç–æ —Å—Ç—Ä–æ–∫–∞ —Å JSON
const raw = $json.output ?? $json.response ?? $json.data ?? $json.message ?? $json.text ?? null;

let weight_kg = null;
let weight_bucket = null; // 'lt_60' | '60_100' | 'gt_100' | null
let knows_project = null; // true | false | null
let follow_ok = 'no';     // 'yes' | 'no'

if (raw) {
  try {
    const parsed = (typeof raw === 'string') ? JSON.parse(raw) : raw;

    // weight_kg
    if (typeof parsed.weight_kg === 'number') {
      weight_kg = parsed.weight_kg;
    } else if (typeof parsed.weight_kg === 'string' && parsed.weight_kg.trim() !== '') {
      const n = Number(parsed.weight_kg.replace(',', '.'));
      weight_kg = Number.isFinite(n) ? n : null;
    }

    // weight_bucket (–µ—Å–ª–∏ –∞–≥–µ–Ω—Ç –Ω–µ –ø—Ä–∏—Å–ª–∞–ª ‚Äî –≤—ã—á–∏—Å–ª–∏–º —Å–∞–º–∏ –∏–∑ weight_kg)
    if (['lt_60','60_100','gt_100'].includes(parsed.weight_bucket)) {
      weight_bucket = parsed.weight_bucket;
    } else if (typeof weight_kg === 'number') {
      if (weight_kg > 100) weight_bucket = 'gt_100';
      else if (weight_kg > 60) weight_bucket = '60_100';
      else weight_bucket = 'lt_60';
    } else {
      weight_bucket = null;
    }

    // knows_project
    if (parsed.knows_project === true || parsed.knows_project === false || parsed.knows_project === null) {
      knows_project = parsed.knows_project;
    } else if (typeof parsed.knows_project === 'string') {
      const s = parsed.knows_project.trim().toLowerCase();
      knows_project = (s === 'true') ? true : (s === 'false' ? false : null);
    }

    // follow_ok
    if (parsed.follow_ok === 'yes' || parsed.follow_ok === 'no') {
      follow_ok = parsed.follow_ok;
    }
  } catch (e) {
    // –æ—Å—Ç–∞–≤–∏–º –¥–µ—Ñ–æ–ª—Ç—ã
  }
}

return [{
  ...$json,
  weight_kg,
  weight_bucket,
  knows_project,
  follow_ok
}];

