let o = {};
try { o = JSON.parse($json.message?.content || '{}'); } catch(e) {}

const base64 = $node["HTTP Request1"].json?.base64 
            || $node["HTTP Request1"].json?.result?.base64 
            || null;

const mimetype = $node["HTTP Request1"].json?.mimetype || 'image/jpeg';
const fileName = $node["HTTP Request1"].json?.fileName || (mimetype === 'application/pdf' ? 'receipt.pdf' : 'receipt.jpg');

return [{
  // –∏–∑ OpenAI5
  receipt: o.receipt || 'uncertain',
  paid: o.paid || 'uncertain',
  amount: Number.isFinite(o.amount) ? o.amount : null,
  method: o.method || 'unknown',

  // —Å–µ—Ä–≤–∏—Å–Ω—ã–µ –ø–æ–ª—è
  is_paid: (o.paid === 'yes'),
  name: $node["Webhook"].json.body?.data?.pushName || '',
  phone: String($node["Webhook"].json.body?.data?.key?.remoteJid || $node["Webhook"].json.body?.sender || '').split('@')[0].replace(/\D/g,''),
  caption_or_text: $node["Webhook"].json.body?.data?.message?.imageMessage?.caption 
                 || $node["Webhook"].json.body?.data?.message?.conversation || '',

  // –¥–ª—è –æ—Ç–ø—Ä–∞–≤–∫–∏ —Ñ–∞–π–ª–∞
  base64,
  mimetype,
  fileName
}];

// –ò—Å—Ç–æ—á–Ω–∏–∫: –ø—ã—Ç–∞–µ–º—Å—è –≤–∑—è—Ç—å –¥–∞–Ω–Ω—ã–µ –∏–∑ Edit Fields1, –∏–Ω–∞—á–µ –∏–∑ —Ç–µ–∫—É—â–µ–≥–æ $json
const src = ($items("Edit Fields1")[0]?.json) || $json || {};

// –§—É–Ω–∫—Ü–∏–∏-–ø–æ–º–æ—â–Ω–∏–∫–∏
const onlyDigits = v => String(v || '').split('@')[0].replace(/\D/g, '');
const pick = (...xs) => xs.find(v => v !== undefined && v !== null && String(v).trim() !== '');

// –î–æ—Å—Ç–∞—ë–º —Ü–∏—Ñ—Ä—ã (–ø—Ä–∏–æ—Ä–∏—Ç–µ—Ç: remoteJid –∫–æ–Ω—Ç–∞–∫—Ç–∞, –∑–∞—Ç–µ–º sender/from –∏ —Ç.–ø.)
let digits = onlyDigits(
  pick(
    src.contact_id,
    src.phone,
    src.body?.data?.key?.remoteJid,
    src.body?.sender,
    src.body?.from,
    src.from,
    src.sender,
    src.data?.key?.remoteJid,
    (src.key || '').split(':')[0] // –Ω–∞ –∫—Ä–∞–π–Ω–∏–π —Å–ª—É—á–∞–π ‚Äî –ø—Ä–µ—Ñ–∏–∫—Å key –¥–æ ':'
  )
);

// –ë–∞–∑–æ–≤—ã–µ –ø–æ–ª—è
const stage = src.stage || 'bmi';
const now   = Number.isFinite(+src.now) ? +src.now : Date.now();

// –ö–ª—é—á: —Ñ–æ—Ä–º–∏—Ä—É–µ–º –∏–∑ digits:stage, –µ—Å–ª–∏ digits –Ω–µ—Ç ‚Äî –∫–∞–∫ –º–∏–Ω–∏–º—É–º :stage
const key = digits ? `${digits}:${stage}` : (src.key || `:${stage}`);

// –¢–µ–∫—Å—Ç –≤—Ö–æ–¥—è—â–µ–≥–æ –∫—É—Å–∫–∞ (–µ—Å–ª–∏ Edit Fields1 –µ–≥–æ –Ω–µ –¥–∞–ª ‚Äî —Ç—è–Ω–µ–º –∏–∑ webhook)
const text = pick(
  src.text,
  src.body?.data?.message?.conversation,
  src.body?.text,
  ''
).toString().trim();

// –ò–º—è: —É—á–∏—Ç—ã–≤–∞–µ–º pushName –≤ body.data
const name = pick(
  src.name,
  src.pushName,
  src.body?.data?.pushName,
  src.body?.pushName,
  ''
).toString().trim();

// –°—Ç–∞—Ä—Ç–æ–≤—ã–π –±—É—Ñ–µ—Ä
const bufferArr = text ? [{ text, ts: now }] : [];

return [{
  json: {
    key,
    phone: digits,         // —Ç–æ–ª—å–∫–æ —Ü–∏—Ñ—Ä—ã
    stage,
    firstAt: now,
    lastAt:  now,
    buffer: JSON.stringify(bufferArr),
    name,                  // –∫–æ–ª–æ–Ω–∫–∞ name ‚Äî –æ–ø—Ü–∏–æ–Ω–∞–ª—å–Ω–æ, –Ω–æ –ø–æ–ª–µ–∑–Ω–æ
  }
}];

// –ò—Å—Ç–æ—á–Ω–∏–∫–∏
const src = ($items("Edit Fields1")[0]?.json) || $json || {};
const row = $items("Google Sheets13")[0]?.json || {};

// –•–µ–ª–ø–µ—Ä—ã
const onlyDigits = v => String(v || '').split('@')[0].replace(/\D/g, '');
const pick = (...xs) => xs.find(v => v !== undefined && v !== null && String(v).trim() !== '');

// –í—Ä–µ–º—è/—ç—Ç–∞–ø
const now   = Number.isFinite(+src.now) ? +src.now : Date.now();
const stage = row.stage || src.stage || 'bmi';

// –ù–æ–º–µ—Ä: —Å–ø–µ—Ä–≤–∞ –∏–∑ —Å—Ç—Ä–æ–∫–∏ (–Ω–∞–¥–µ–∂–Ω–µ–µ), –∑–∞—Ç–µ–º –∏–∑ –≤—Ö–æ–¥–∞, –∑–∞—Ç–µ–º –∏–∑ key
let digits = onlyDigits(
  pick(
    row.phone,
    src.contact_id,
    src.phone,
    src.body?.data?.key?.remoteJid,
    src.body?.sender,
    src.body?.from,
    src.from,
    src.sender,
    src.data?.key?.remoteJid,
    (row.key || src.key || '').split(':')[0]
  )
);

// –ö–ª—é—á –æ—Å—Ç–∞–≤–ª—è–µ–º –ø—Ä–µ–∂–Ω–∏–π, –µ—Å–ª–∏ –µ—Å—Ç—å; –∏–Ω–∞—á–µ —Å–æ–±–∏—Ä–∞–µ–º –∏–∑ digits:stage
const key = row.key || (digits ? `${digits}:${stage}` : (src.key || `:${stage}`));

// –¢–µ–∫—Å—Ç –∫—É—Å–∫–∞
const text = pick(
  src.text,
  src.body?.data?.message?.conversation,
  src.body?.text,
  ''
).toString().trim();

// –¢–µ–∫—É—â–∏–π –±—É—Ñ–µ—Ä –∏–∑ —Å—Ç—Ä–æ–∫–∏
let arr = [];
try { arr = row.buffer ? JSON.parse(row.buffer) : []; } catch (_) { arr = []; }

// –ê–Ω—Ç–∏–¥—É–±–ª—å –ø–æ –ø–∞—Ä–µ (ts, text)
// –ü—Ä–∏–º–µ—á–∞–Ω–∏–µ: –µ—Å–ª–∏ –æ–ø–∞—Å–∞–µ—à—å—Å—è –∫–æ–ª–ª–∏–∑–∏–π –ø–æ ts, –º–æ–∂–Ω–æ –¥–æ–±–∞–≤–∏—Ç—å —Ö—ç—à —Ç–µ–∫—Å—Ç–∞.
const chunk   = { text, ts: now };
const exists  = arr.some(x => String(x.ts) === String(chunk.ts) && (x.text || '') === chunk.text);
const nextArr = text ? (exists ? arr : [...arr, chunk]) : arr;

// –ò–º—è: –±–µ—Ä—ë–º –∏–∑ —Å—Ç—Ä–æ–∫–∏, –∏–Ω–∞—á–µ –∏–∑ webhook (—É—á–∏—Ç—ã–≤–∞–µ–º body.data.pushName)
const name = pick(
  row.name,
  src.name,
  src.pushName,
  src.body?.data?.pushName,
  src.body?.pushName,
  ''
).toString().trim();

return [{
  json: {
    key,
    phone: digits,
    stage,
    firstAt: row.firstAt || now,
    lastAt:  now,                        // –≤–∞–∂–Ω–æ: –æ–±–Ω–æ–≤–ª—è–µ–º –Ω–∞ –∫–∞–∂–¥—ã–π —á–∞–Ω–∫
    buffer: JSON.stringify(nextArr),
    name,
  }
}];

// –±–µ–∑–æ–ø–∞—Å–Ω—ã–π –¥–æ—Å—Ç—É–ø –∫ json –ª—é–±–æ–≥–æ —É–∑–ª–∞ (–µ—Å–ª–∏ –Ω–µ –∏—Å–ø–æ–ª–Ω—è–ª—Å—è ‚Äî –≤–µ—Ä–Ω—ë—Ç {})
const get = (name) => {
  try { const a = $items(name); return (Array.isArray(a) && a.length) ? a[0].json : {}; }
  catch(e){ return {}; }
};

let o = {};
try { o = JSON.parse($json.message?.content || '{}'); } catch(e) {}

const hr = get("HTTP Request1");   // –≤–º–µ—Å—Ç–æ $node["HTTP Request1"]
const base64   = hr.base64 || hr.result?.base64 || null;
const mimetype = hr.mimetype || 'image/jpeg';
const fileName = hr.fileName || (mimetype === 'application/pdf' ? 'receipt.pdf' : 'receipt.jpg');

return [{
  // –∏–∑ OpenAI5
  receipt: o.receipt || 'uncertain',
  paid: o.paid || 'uncertain',
  amount: Number.isFinite(o.amount) ? o.amount : null,
  method: o.method || 'unknown',

  // —Å–µ—Ä–≤–∏—Å–Ω—ã–µ –ø–æ–ª—è
  is_paid: (o.paid === 'yes'),
  name: $items("Webhook")[0]?.json?.body?.data?.pushName || '',
  phone: String(($items("Webhook")[0]?.json?.body?.data?.key?.remoteJid
              || $items("Webhook")[0]?.json?.body?.sender || ''))
           .split('@')[0].replace(/\D/g,''),
  caption_or_text: $items("Webhook")[0]?.json?.body?.data?.message?.imageMessage?.caption
                || $items("Webhook")[0]?.json?.body?.data?.message?.conversation || '',

  // –¥–ª—è –æ—Ç–ø—Ä–∞–≤–∫–∏ —Ñ–∞–π–ª–∞
  base64, mimetype, fileName
}];
