parameters.sessionKey:
={{ 
  (() => {
    const b = $json.body || {};
    const k = (b.conversation_key ?? '').toString().trim();
    if (k) return k;
    const client = (b.client_account_id ?? '').toString().trim();
    const biz    = (b.business_account_id ?? '').toString().trim();
    if (client && biz) return `${client}_${biz}`;
    return String($json.session_key || $json.sessionId || Date.now());
  })()
}}

