// ---------- helper -----------
function cleanUrl(u) {
	if (!u) return '';

	// ── 1. Уже «правильный» прямой UC-URL ───────────────────────────
	if (u.includes('drive.google.com/uc?')) {
		return u.split('&')[0];               // обрезаем лишние параметры
	}

	// ── 2. Формат share-link:  .../file/d/FILE_ID/view?usp=… ─────────
	let m = u.match(/\/file\/d\/([\w-]+)/);
	if (m) {
		return `https://drive.google.com/uc?export=download&id=${m[1]}`;
	}

	// ── 3. Старый формат open?id=FILE_ID … ───────────────────────────
	m = u.match(/[?&]id=([\w-]+)/);
	if (m) {
		return `https://drive.google.com/uc?export=download&id=${m[1]}`;
	}

	// ── 4. Любой внешний URL → обрезаем хвост ?w=…&h=…&format=… ─────
	return u.split('?')[0];
}

// ------------ main ------------
const row       = items[0].json;
const rowNumber = items[0].json.row_number;

const data = {
	// основные поля
	model               : row['Model'],
	year                : String(row['Year']),
	price               : String(row['Price']),
	length_overall      : row['Length overall'],
	maximum_beam        : row['Maximum beam'],
	draft               : row['Draft'],
	displacement        : row['Displacement'],
	fuel_capacity       : row['Fuel capacity'],
	water_capacity      : row['Fresh water capacity'],
	engines             : row['Engines'],
	total_power         : String(row['Total power']),
	engine_hours        : String(row['Engine hours']),
	class               : row['Class'],
	propulsion          : row['Propulsion'],
	capacity            : row['Capacity'],
	max_cruising_speed  : row['Max/Cruising Speed'],
	description         : row['Description'],
	advance_package     : row['Advance package'],
	options_main_system : row['Options main system'],
	exterior            : row['Exterior'],
	interior            : row['Interior'],
	appliances          : row['Appliances'],
	electronics         : row['Electronics'],
	audio_video         : row['Audio video'],

	// менеджер
	Manager             : row['Manager'],

	// тех-поля для Update Row
	row_number          : rowNumber,
	'Отметка времени'   : String(row['Отметка времени'])
};

// фото 1–31
for (let i = 1; i <= 31; i++) {
	const col = `Photo${i} URL`;
	if (row[col]) data[`photo${i}`] = cleanUrl(row[col]);
}

data.completed = true;

return [{ json: data }];
