// alt_0_7  →  parts = ['alt','0','7']
const [, idx, id] = $json.callback_query.data.split('_');

return [{
  json: {
    chat_id    : $json.callback_query.message.chat.id,   // ID чата, где лежит сообщение-клавиатура
    draft_id   : Number(id),                             // 7
    alt_idx    : Number(idx),                            // 0 / 1 / 2
    del_msg_id : $json.callback_query.message.message_id // ID сообщения, которое надо удалить
  }
}];

// weight_200_7  →  parts = ['weight','200','7']
const [, grams, id] = $json.callback_query.data.split('_');

return [{
  json: {
    chat_id    : $json.callback_query.message.chat.id,   // тот же chat_id
    draft_id   : Number(id),                             // 7
    grams      : Number(grams),                          // 200
    del_msg_id : $json.callback_query.message.message_id // ID клавиатуры веса
  }
}];

/**
 * INPUT ожидает:
 *   • items[0].json  — строка-row из Supabase (`draft`)
 *   • $json.alt_idx  — 0 / 1 / 2  (пришло из «alt_0_<id>»)
 *
 * Правка: alt_idx указывает на элемент candidates[1…3],
 * поэтому делаем сдвиг +1.
 */

// 0. Достаём draft: бывает либо объектом, либо массивом с одним элементом
const draft = Array.isArray(items[0].json)
  ? items[0].json[0]
  : items[0].json;

// 1. candidates: если это строка — парсим
const candidates = typeof draft.candidates === 'string'
  ? JSON.parse(draft.candidates)
  : draft.candidates || [];

// 2. выбираем нужную альтернативу (alt_idx + 1)
const offs = ($json.alt_idx ?? 0) + 1;
const cand = candidates[offs] || {
  dish  : '(не найдено)',
  grams : draft.grams_pred || 200,
};

// 3. считаем три варианта массы
const g    = Number(cand.grams) || 200;
const step = g < 120 ? 20 : 50;
const btns = [g - step, g, g + step].filter(x => x > 0);

return [{
  json: {
    chat_id   : draft.chat_id,
    draft_id  : draft.message_id,           // пригодится для UPDATE
    dish_ru   : cand.dish,
    grams_est : g,
    buttons   : btns.map(x => ({
      text : `${x} г`,
      data : `weight_${x}_${draft.message_id}`,
    })),
  },
}];

/** INPUT ожидает:
 *  • items[0].json  ← строка из Supabase (`draft`)
 *  • $json.alt_idx  ← 0 / 1 / 2  (пришло из «alt_0_id»)
 */

/* 0. draft может лежать либо прямо в .json, либо в .json[0]
      (зависит от Always Output Data) */
const draft = Array.isArray(items[0].json)
  ? items[0].json[0]
  : items[0].json;

/* 1. candidates: если строка → JSON.parse */
const candidates = typeof draft.candidates === 'string'
  ? JSON.parse(draft.candidates)
  : draft.candidates || [];

/* 2. индекс альтернативы */
const idx = $json.alt_idx ?? 0;
const cand = candidates[idx] || { dish:'(не найдено)', grams: draft.grams_pred || 200 };

/* 3. считаем кнопки веса */
const g    = cand.grams;
const step = g < 120 ? 20 : 50;
const btns = [g-step, g, g+step].filter(x=>x>0);

return [{
  json:{
    chat_id   : draft.chat_id,
    draft_id  : draft.message_id,        // пригодится для UPDATE
    dish_ru   : cand.dish,
    grams_est : g,
    buttons   : btns.map(x=>({
                  text : `${x} г`,
                  data : `weight_${x}_${draft.message_id}`
               }))
  }
}];

/**
 *  Упрощаем: код лишь формирует данные,
 *  а финальный текст собираем прямо в Telegram-узле.
 */

// 0. Достаём draft (учёт Always-Output Data)
const raw = items[0].json[0] ?? items[0].json;

// 1. Парсим candidates, если пришла строкой
const candidates = typeof raw.candidates === 'string'
  ? JSON.parse(raw.candidates)
  : raw.candidates || [];

// 2. Берём альтернативы (макс. 3) — без первого
const alts = candidates.slice(1, 4);

// 3. Подготовим «красивые» строки для списка
const altLines = alts.map((c, i) => `${i + 1}. ${c.dish}`);

// 4. Кнопки 1-2-3
const buttons = alts.map((_, i) => ({
  text : String(i + 1),
  data : `alt_${i}_${raw.message_id}`,
}));

return [{
  json: {
    chat_id   : raw.chat_id,
    draft_id  : raw.message_id,

    // для Telegram-узла
    alt_lines : altLines,   // массив ['1. …', '2. …', …]
    buttons,                // inline-кнопки
  },
}];

// Пример callback_data:  dish_yes_7
const parts   = $json.callback_query.data.split('_');
const draftId = parts.pop();                        // '7'

// ID сообщения с кнопками «Да / Нет»,
// которое нужно удалить после обработки
const delMsgId = $json.callback_query.message.message_id;

return [{
  json: {
    draft_id   : Number(draftId),                 // 7
    chat_id    : $json.callback_query.from.id,    // 7758…
    del_msg_id : delMsgId                         // для Delete-узла
  }
}];

// Пример callback_data:  dish_yes_7
const parts   = $json.callback_query.data.split('_');
const draftId = parts.pop();                        // '7'

// ID сообщения с кнопками «Да / Нет»,
// которое нужно удалить после обработки
const delMsgId = $json.callback_query.message.message_id;

return [{
  json: {
    draft_id   : Number(draftId),                 // 7
    chat_id    : $json.callback_query.from.id,    // 7758…
    del_msg_id : delMsgId                         // для Delete-узла
  }
}];

// Parse macros  (замена текущего кода)
const sw = $items('Split weight')[0].json;   // chat_id, grams, draft_id …

// assistant.content уже объект: dish, kcal, prot, …
const gpt = $json.message?.content ?? $json; // на случай, если jsonOutput=false

const toMsk = () => {
  const msk = new Date(new Date().toLocaleString('en-US',{timeZone:'Europe/Moscow'}));
  const p = n => String(n).padStart(2,'0');
  return `${msk.getFullYear()}-${p(msk.getMonth()+1)}-${p(msk.getDate())}` +
         `T${p(msk.getHours())}:${p(msk.getMinutes())}:00+03:00`;
};

return [{
  json: {
    ...sw,          // chat_id, grams …
    ...gpt,         // dish, kcal, prot …
    eaten_at: toMsk()
  }
}];

/**
 * Берём исходный update из триггера
 */
const upd = $items("Telegram Trigger")[0].json.message;
const chatId  = upd.chat.id;
const msgId   = upd.message_id;
const photoId = upd.photo[upd.photo.length - 1].file_id;

/**
 * Русский JSON от Vision
 */
const vision = JSON.parse(($json.content || '').replace(/```/g, '').trim());
const candidates = vision.candidates || [];
const first      = candidates[0] || { dish:'блюдо', grams:0 };

/**
 * Draft‑объект
 */
return [{
  json: {
    chat_id       : chatId,
    message_id    : msgId,
    photo_file_id : photoId,

    candidates,              // массив на русском
    grams_pred : first.grams,
    status     : 'await_dish',

    text   : `Это *${first.dish}*?`,
    cb_yes : `dish_yes_${msgId}`,
    cb_no  : `dish_no_${msgId}`
  }
}];

// cancel_62
const mealId = Number($json.callback_query.data.split('_').pop());

return [{
  json:{
    meal_id : mealId,
    chat_id : $json.callback_query.from.id,
    msg_id  : $json.callback_query.message.message_id
  }
}];

const row = $items('GET meals')[0].json;      // dish, grams, kcal …
const ctx = $items('Parse cancel')[0].json;   // chat_id, msg_id

return [{
  json: {
    chat_id : ctx.chat_id,
    msg_id  : ctx.msg_id,

    // что пойдёт в текст
    dish  : row.dish,
    grams : row.grams,
    kcal  : row.kcal,
    prot  : row.prot,
    fat   : row.fat,
    carb  : row.carb
  }
}];

